import React, { Component, Fragment } from 'react';
import '../asset/css/animate.css';
import '../asset/css/style.css';
import '../asset/css/responsive.css';
import WOW from 'wowjs';
import TopBanner from "../components/TopBanner/TopBanner";
import Brand from "../components/ClientProject/Brand";
import AboutUs from '../components/AboutUs/AboutUs';
import Services from '../components/Services/Services';
import Features from '../components/Features/Features';
import Skill from '../components/Skill/Skill';
import Strength from '../components/Strength/Strength';
import Faq from '../components/Faq/Faq';
import Video from '../components/Video/Video';
import Team from '../components/Team/Team';
import Contact from '../components/Contact/Contact';
import BlogPost from '../components/Blog/BlogPost';
import Footer from '../components/Footer/Footer';
import MobileNav from '../components/MobileNav/MobileNav';

class HomePage extends Component {


  componentDidMount() {
    new WOW.WOW({
      live: false
    }).init();

    let aboutItem = document.querySelector('.navAnimated li:nth-child(1)');
    let servicesItem = document.querySelector('.navAnimated li:nth-child(2)');
    let strengthItem = document.querySelector('.navAnimated li:nth-child(3)');
    let teamItem = document.querySelector('.navAnimated li:nth-child(4)');
    let blogItem = document.querySelector('.navAnimated li:nth-child(5)');

    const noCLass = document.querySelector('.navAnimated li');
    noCLass.classList.remove('active');

    const aboutOffset = document.getElementById('about').offsetTop;
    const aboutHeight = document.getElementById('about').clientHeight;

    const servicesOffset = document.getElementById('service').offsetTop;
    const servicesHeight = document.getElementById('service').clientHeight;

    const strengthOffset = document.getElementById('strength').offsetTop;
    const strengthHeight = document.getElementById('strength').clientHeight;

    const teamOffset = document.getElementById('team').offsetTop;
    const teamHeight = document.getElementById('team').clientHeight;

    const blogOffset = document.getElementById('blog').offsetTop;
    const blogHeight = document.getElementById('blog').clientHeight;

    window.addEventListener('scroll',()=> {
      if(window.pageYOffset >= (aboutOffset-aboutHeight / 3)) {
        aboutItem.classList.add('active');

        servicesItem.classList.remove('active');
        strengthItem.classList.remove('active');
        teamItem.classList.remove('active');
        blogItem.classList.remove('active');
      }
      if(window.pageYOffset >= (servicesOffset-servicesHeight / 3)) {
        servicesItem.classList.add('active');

        aboutItem.classList.remove('active');
        strengthItem.classList.remove('active');
        teamItem.classList.remove('active');
        blogItem.classList.remove('active');
      }
      if(window.pageYOffset >= (strengthOffset-strengthHeight / 3)) {
        strengthItem.classList.add('active');

        aboutItem.classList.remove('active');
        servicesItem.classList.remove('active');
        teamItem.classList.remove('active');
        blogItem.classList.remove('active');
      }
      if(window.pageYOffset >= (teamOffset-teamHeight / 3)) {
        teamItem.classList.add('active');

        aboutItem.classList.remove('active');
        servicesItem.classList.remove('active');
        blogItem.classList.remove('active');
        strengthItem.classList.remove('active');
      }
      if(window.pageYOffset >= (blogOffset-blogHeight / 3)) {
        blogItem.classList.add('active');

        aboutItem.classList.remove('active');
        teamItem.classList.remove('active');
        servicesItem.classList.remove('active');
        strengthItem.classList.remove('active');
      }

    })


  }

  render() {
    return (
      <Fragment>
        <MobileNav/>
        <TopBanner />
        <Brand/>
        <AboutUs/>
        <Services/>
        <Features/>
        <Skill/>
        <Strength/>
        <Faq/>
        <Video/>
        <Team/>
        <Contact/>
        <BlogPost/>
        <Footer/>
      </Fragment>
    );
  }
}

export default HomePage;
