import React, { Component,Fragment } from 'react'
import logo_right from '../../asset/images/logo_right.png';
import icon_fb from '../../asset/images/social/icon_fb.png';
import icon_twitter from '../../asset/images/social/icon_twitter.png';
import icon_linkedin from '../../asset/images/social/icon_linkedin.png';
import icon_instagram from '../../asset/images/social/icon_instagram.png';
import icon_youtube from '../../asset/images/social/icon_youtube.png';
import icon_medium from '../../asset/images/social/icon_medium.png';


class MobileNav extends Component {

  onScroll = () => {
      if (window.scrollY > 100) {
          document.getElementById('mobileNavbar').classList.add('stickyNav');
      }
      else if (window.scrollY < 100) {
         document.getElementById('mobileNavbar').classList.remove('stickyNav');
      }
  }

  componentDidMount() {
    window.addEventListener('scroll', this.onScroll);
  }

  render() {
    return (
      <Fragment>
        <nav id="mobileNavbar">
            <div className="spLogo">
              <a href="/"><img src={logo_right} alt="sp-logo"/></a>
            </div>
            <div className="navIcon" id="openNav"><p>â˜°</p></div>
        </nav>
        <div id="mySidenav" className="sidenav">
            <a href="javascript:void(0)" className="closebtn" id="closeNav">&times;</a>
            <ul className="spMenuLinks">
                <li><a href="#about" data-hover="About Us">About Us</a></li>
                <li><a href="#service" data-hover="Services">Services</a></li>
                <li><a href="#strength" data-hover="Strength">Strength</a></li>
                <li><a href="#team" data-hover="Team">Team</a></li>
                <li><a href="#blog" data-hover="Blog">Blog</a></li>
                <li className="btnGetInTouch"><a href="#contact-us">Get in touch</a></li>
            </ul>
            <div className="snsLink">
                <h2>Following Us</h2>
                <div className="images">
                    <a href="https://www.facebook.com/CybridgeBangladesh" target="_blank"><img src={icon_fb} alt="icon_fb" /></a>
                    <a href="https://twitter.com/cybridge" target="_blank"><img src={icon_twitter}  alt="icon_twitter" /></a>
                    <a href="https://www.linkedin.com/company/cybridge-bangladesh" target="_blank"><img src={icon_linkedin}  alt="icon_linkedin" /></a>
                    <a href="https://www.instagram.com/accounts/login/?next=/cybridgebangladesh/" target="_blank"><img src={icon_instagram} alt="icon_instagram" /></a>
                    <a href="https://www.youtube.com/channel/UCsfoAPEMIpsG8E9Q4z7hdCg" target="_blank"><img src={icon_youtube} alt="icon_youtube" /></a>
                    <a href="https://medium.com/oceanize-geeks" target="_blank"><img src={icon_medium} alt="icon_medium" /></a>
                </div>
            </div>
        </div>
      </Fragment>
    )
  }
}
export default  MobileNav;
