import React, {useEffect } from 'react';
import $ from 'jquery';
import icon_left_arrow from '../../asset/images/ourteam/icon_left_arrow.png';

export default function ScrollTop() {

  // function pageTop(e) {
  //   e.preventDefault();
  //   $("html,body").animate({
  //     scrollTop: 0
  //   });
  //   return false;
  // }

  pageTop = (event) => {
    event.preventDefault();
    $("html,body").animate({scrollTop: 0});
    return false;
  }


  function logit() {
    if ($(window).scrollTop()>150){
      $('.scrollToTop').show(1000);
    }else{
      $('.scrollToTop').hide(1000);
    }
  }

  useEffect(() => {
    $('.scrollToTop').hide();
    function watchScroll() {
      window.addEventListener("scroll", logit);
    }
    watchScroll();
  },[]);



  return (
    <div className="scrollToTop"><a href="#" onClick={pageTop}><img src={icon_left_arrow} alt="icon_left_arrow"/></a></div>
  )
}
